import Web3 from 'web3'
import { provider } from 'web3-core'
import { useWallet } from 'use-wallet'

const { ethereum }: { ethereum: provider } = useWallet()
const web3 = new Web3(ethereum)

const erc20ABI = require('../assets/abi/ERC20ABI.json');

export const addresses = {
    'ERC20': {
        1: '0xd9bE442ACe8665C6046B1D365BF1C20a2D75Fd0f',
        3: '',
        42: ''
    }
}

export const getAddress = (val: string) => {
    return addresses[val]
}

const erc20Address = getAddress('ERC20')[1];
const erc20Contract = new web3.eth.Contract(erc20ABI, erc20Address);

export const contracts = {
    ERC20Contract: {
        address: erc20Address,
        contract: erc20Contract,
        decimals: 18
    }
}

export const getContract = (val: string) => {
    const contractName = val + 'Contract';
    return contracts[contractName];
}
